const as = require('./')
let config = { hosts: '127.0.0.1' } // '192.168.33.10' }
as.connect(config, (err, client) => {
  if (err) console.error('client 1', config, err.message)
  // else console.log('client 1 connected successfully')
  if (client) client.close(false)
  let config2 = { hosts: '127.0.0.1' } // invalid address
  as.connect(config2, (err, client2) => {
    if (err) console.error('client 2', config, err.message)
    // else console.log('client 2 connected successfully')
    if (client2) client2.close(false)
    as.releaseEventLoop()
  })
})
